'pub_user', 'CREATE TABLE `pub_user` (\n  `user_id` varchar(50) NOT NULL COMMENT \'用户id\',\n  `user_name` varchar(100) NOT NULL COMMENT \'用户名称\',\n  `bill_code` varchar(20) NOT NULL COMMENT \'编码\',\n  `nick_name` varchar(50) DEFAULT NULL COMMENT \'尼称\',\n  `mail` varchar(100) DEFAULT NULL COMMENT \'邮箱\',\n  `phone` varchar(50) DEFAULT NULL COMMENT \'手机号\',\n  `birthday` date DEFAULT NULL COMMENT \'生日 \',\n  `tmp_int1` int(11) DEFAULT NULL,\n  `tmp_int2` int(11) DEFAULT NULL,\n  `tmp_int3` int(11) DEFAULT NULL,\n  `tmp_int4` int(11) DEFAULT NULL,\n  `tmp_var1` varchar(50) DEFAULT NULL,\n  `tmp_var2` varchar(50) DEFAULT NULL,\n  `tmp_var3` varchar(50) DEFAULT NULL,\n  `tmp_var4` varchar(50) DEFAULT NULL,\n  `ts` varchar(20) DEFAULT NULL COMMENT \'时间戳\',\n  `sex` varchar(10) DEFAULT NULL COMMENT \'性别\',\n  `pic_head` varchar(100) DEFAULT NULL COMMENT \'头像\',\n  `password` varchar(100) DEFAULT NULL COMMENT \'密码\',\n  PRIMARY KEY (`user_id`),\n  UNIQUE KEY `bill_code_UNIQUE` (`bill_code`),\n  UNIQUE KEY `mail_UNIQUE` (`mail`),\n  UNIQUE KEY `phone_UNIQUE` (`phone`)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8'
